import Header from './Header'
import { withRouter } from 'react-router-dom'
import { Card, Button, Container } from 'react-bootstrap'
import { useEffect, useState } from 'react'
function BookDetail(props) {

    console.warn("props", props.match.params.id)
    const [data, setData] = useState([])
    useEffect(async () => {
        let result = await fetch("http://localhost:8000/api/product/" + props.match.params.id);
        result = await result.json();
        setData(result);
    })
    return (
        <div>
            <Header />
            <Container>
                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src={"http://localhost:8000/" + data.file_path} />                  
                    <Card.Body>
                        <Card.Title>{data.name}</Card.Title>
                        <Card.Text>Author: {data.author}</Card.Text>
                        <Card.Text>Pages: {data.no_of_pages}</Card.Text>
                        <Card.Text>Price: â‚¹ {data.price}</Card.Text>
                        <Button variant="primary">Buy Now</Button>
                    </Card.Body>
                </Card>
            </Container>

        </div>

    )
}
export default withRouter(BookDetail)
